package test0822;

public class Test0913 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("홍길동");
		
		//숫자형 변수 a의 값은 1입니다.
		//a의 값이 1이면 참이고 2이면 거짓입니다. 라는 코드를 작성해 보세요
		
		int a = 1;
		if(a == 1) {
			System.out.println("참");
		}else if(a == 2) {
			System.out.println("거짓");
		}
		
		
		
		// for문
		// while 문
		// do while문 처음은 무조건 실행
		
		for(int i = 1; i <= 10; i++) {
			System.out.println("1");
		}
//		초기식;
//		while(증가식) {
//			반복실;
//		}
		
		int i = 1;
		while(i<=10) {
			System.out.println("2");
			i++;
		}
		
	
		//1에서 열번을 출력 하시오
		

	}

}
